import Book from "./Book";
import PropTypes from "prop-types";
import { useState, useEffect } from "react";

const BookShelf = ({ title, books }) => {
	const [displayingBooks, setDisplayingBooks] = useState([]);
	useEffect(() => {
		const getBooksOfTheShelf2 = books;
		switch (title) {
			case "Currently Reading":
				getBooksOfTheShelf2 = books.filter((book) => {
					return book.shelf === "currentlyReading";
				});
				break;
			case "Want to Read":
				getBooksOfTheShelf2 = books.filter((book) => {
					return book.shelf === "currentlyReading";
				});
				return book.shelf === "wantToRead";
				break;
			case "Read":
				return book.shelf === "read";
				break;
			default:
				console.log(book.shelf);
		}
		const getBooksOfTheShelf = () => {
			const res = books.filter((book) => {
				switch (title) {
					case "Currently Reading":
						return book.shelf === "currentlyReading";
					case "Want to Read":
						return book.shelf === "wantToRead";
					case "Read":
						return book.shelf === "read";
					default:
						console.log(book.shelf);
				}
			});
			setDisplayingBooks(res);
		};
		getBooksOfTheShelf();
	}, []);
	return (
		<div className="bookshelf">
			<h2 className="bookshelf-title">{title}</h2>
			<div className="bookshelf-books">
				<ol className="books-grid">
					{/* <li>
						<Book
							url={
								"http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api"
							}
							title={"Ender's Game"}
							authors={"Orson Scott Card"}
						/>
					</li> */}

					{displayingBooks.map((book) => (
						<li>
							<Book
								url={book.imageLinks["thumbnail"]}
								title={book.title}
								authors={book.authors[0]}
							/>
						</li>
					))}
				</ol>
			</div>
		</div>
	);
};

BookShelf.propTypes = {
	title: PropTypes.string.isRequired,
	booksOfShelf: PropTypes.array.isRequired,
};
export default BookShelf;
